                    investmentTypeResults.Add(latestEntry.InvestmentType, new(latestEntry.InvestmentType.ToString(), latestEntry.Value * pricePerUnit));
                else
                    existingResult.Value += latestEntry.Value * pricePerUnit;
            }
        }

